mapper.select:
SELECT * FROM
TB_PERSON
ORDER BY NAME ASC;

mapper.update:
UPDATE TB_PERSON
SET NAME=#{name}, GENDER=#{gender}, AGE=#{age}, PHONE=#{phone}, ADDRESS=#{address}
WHERE SEQ=#{seq}

update.foreach:
UPDATE TB_PERSON
SET NAME=#{name}, GENDER=#{gender}, AGE=#{age}, PHONE=#{phone}, ADDRESS=#{address}
WHERE SEQ=#{seq}
