mapper.insert:
 		INSERT INTO address_book (
			name
 			, age
 			, gender
 			, phone
 			, address
 		)
 		VALUES(
 			#{name}
 			, #{age}
 			, #{gender}
 			, #{phone}
 			, #{address}
 		)

mapper.update:
UPDATE 
	address_book 
SET 
	age = #{age}
	, gender=#{gender}
	, phone=#{phone}
	, address=#{address}
WHERE 
	name=#{name}
